"use client";
export default function Sidebar() {
  return (
    <div
      className="h-screen fixed top-0 left-0 w-16 m-0
                      flex flex-col
                      bg-gray-900 text-white shadow-lg"
    >
      <SidebarIcon icon={<HomeIcon />} text="Home" href="/dashboard" />
      <SidebarIcon
        icon={<OrderIcon />}
        text="Payment"
        href="/dashboard/payment"
      />
      <SidebarIcon
        icon={<CategoryIcon />}
        text="Category"
        href="/dashboard/category"
      />
      <SidebarIcon icon={<UserIcon />} text="User" href="/dashboard/user" />
      <SidebarIcon icon={<ShopIcon />} text="Shop" href="/dashboard/shop" />
      <SidebarIcon icon={<ProductIcon />} text="Item" href="/dashboard/item" />

      {/* ... other icons */}
    </div>
  );
}

function SidebarIcon({ icon, text = "tooltip ðŸ’¡", href = "/" }) {
  return (
    <a href={href} onClick={() => console.log(`click ${text}`)}>
      <div className="sidebar-icon group">
        {icon}
        <span className="sidebar-tooltip group-hover:scale-100">{text}</span>
      </div>
    </a>
  );
}

